svg.script:
	var clicked = false;
	var clickedField = undefined;
	var map = undefined;
	var color = 'white';
	var moves = [];
	var timer = undefined;
	var animation = {};
	

/**************************************** moves **************************************/


		function moveOk(field, oldField) {
		    if (oldField.x == field.x && oldField.y == field.y) return false;
			switch (map[oldField.x][oldField.y].type) {
			     case 'pawn' : return movePawnOk(field, oldField);
			     case 'knight' : return moveKnightOk(field, oldField);
			     case 'king' : return moveKingOk(field, oldField);
			     case 'bishop' : return moveBishopOk(field, oldField);
			     case 'rook' : return moveRookOk(field, oldField);
			     case 'queen' : return (moveRookOk(field, oldField) || moveBishopOk(field, oldField));
			     default: return false;
			}
		}
		
		function movePawnOk(destinationField, sourceField) {
		      var factor;
		      var opposColor;
		      if (color == 'white') {
		          factor = 1;
		          opposColor = 'black';
		      } else {
		          factor = -1;
		          opposColor = 'white';		      
		      }
		      
		      if (Math.abs(sourceField.x - destinationField.x) > 1) {
		          return false;
		      }
		      
		      if ((sourceField.y - destinationField.y == factor) && (sourceField.x != destinationField.x)){
		          return map[destinationField.x][destinationField.y].color == opposColor;
		      }
		      
		      if (sourceField.x != destinationField.x)
		          return false;

		      if (sourceField.y - destinationField.y == factor)
		          return map[destinationField.x][destinationField.y].color == undefined;
		      
		      if (sourceField.y - destinationField.y == 2 * factor && map[sourceField.x][sourceField.y].firstMove && map[sourceField.x][sourceField.y - factor].color == undefined){
		          return map[destinationField.x][destinationField.y].color == undefined;
		      }
		      return false;
		}
		
		function moveKingOk(destinationField, sourceField) {
		      if (Math.abs(destinationField.x - sourceField.x) > 1) return false;
		      if (Math.abs(destinationField.y - sourceField.y) > 1) return false;
		      if (map[destinationField.x][destinationField.y].color == color) return false;
		      return true;
		}
		
		function moveKnightOk(destinationField, sourceField) {
		      if (map[destinationField.x][destinationField.y].color == color) return false;
		      var dx = Math.abs(destinationField.x - sourceField.x);
		      var dy = Math.abs(destinationField.y - sourceField.y);
              if (dx == 1) {
                    return (dy == 2);
              } 
              if (dx == 2) {
                    return (dy == 1);
              }
              return false;
		}
		
		function moveBishopOk(destinationField, sourceField) {
              if (map[destinationField.x][destinationField.y].color == color) return false;
		      var dx = Math.abs(destinationField.x - sourceField.x);
		      var dy = Math.abs(destinationField.y - sourceField.y);
		      if (dx != dy) return false;
		      var stepX;
		      var stepY;
		      if (sourceField.x < destinationField.x) {
		      	stepX = 1;
		      } else {
		      	stepX = -1;
		      }
		      if (sourceField.y < destinationField.y) {
		      	stepY = 1;
		      } else {
		      	stepY = -1;
		      }
		      for (var i = 1; i<dx; ++i) {
		      		if (map[sourceField.x + i * stepX][sourceField.y + i * stepY].color != undefined) return false;
		      }
		      return true;
		}

		function moveRookOk(destinationField, sourceField) {
		      if (map[destinationField.x][destinationField.y].color == color) return false;
		      var dx = Math.abs(destinationField.x - sourceField.x);
		      var dy = Math.abs(destinationField.y - sourceField.y);
		      var step;
		      if (dx == 0) {
		          if (sourceField.y < destinationField.y) {
		              step = 1;
		          } else {
		              step = -1;
		          }
		          for (var i = 1; i < dy ; ++i) {
		              //alert(sourceField.x + ", " + (sourceField.y + i * step) + " = " + map[sourceField.x][sourceField.y + i * step].color);
		              if (map[sourceField.x][sourceField.y + i * step].color != undefined) return false;
		          }
		          return true;
		      } 
		      if (dy == 0) {
		          if (sourceField.x < destinationField.x) {
		              step = 1;
		          } else {
		              step=-1;
		          }
		          for (var i = 1; i< dx ; ++i) {
		              if (map[sourceField.x + i * step][sourceField.y].color != undefined) return false;
		          }
		          return true;
		      }
		      return false;
		}










/**************************************** end of moves **************************************/


	function initMap(){
		map = new Array();
		for (var i=0; i<8; ++i) {
			map[i] = new Array()
