				map[i][j] = {};
			}
		}
		map[0][6] = {color:'white', pawn: 'whitePawn1', type: 'pawn', firstMove: true};
		map[1][6] = {color:'white', pawn: 'whitePawn2', type: 'pawn', firstMove: true};
		map[2][6] = {color:'white', pawn: 'whitePawn3', type: 'pawn', firstMove: true};
		map[3][6] = {color:'white', pawn: 'whitePawn4', type: 'pawn', firstMove: true};
		map[4][6] = {color:'white', pawn: 'whitePawn5', type: 'pawn', firstMove: true};
		map[5][6] = {color:'white', pawn: 'whitePawn6', type: 'pawn', firstMove: true};
		map[6][6] = {color:'white', pawn: 'whitePawn7', type: 'pawn', firstMove: true};
		map[7][6] = {color:'white', pawn: 'whitePawn8', type: 'pawn', firstMove: true};
		map[0][7] = {color:'white', pawn: 'whiteRook1', type: 'rook', firstMove: true};
		map[1][7] = {color:'white', pawn: 'whiteKnight1', type: 'knight'};
		map[2][7] = {color:'white', pawn: 'whiteBishop1', type: 'bishop'};
		map[3][7] = {color:'white', pawn: 'whiteQueen1', type: 'queen'};
		map[4][7] = {color:'white', pawn: 'whiteKing1', type: 'king', firstMove: true};
		map[5][7] = {color:'white', pawn: 'whiteBishop2', type: 'bishop'};
		map[6][7] = {color:'white', pawn: 'whiteKnight2', type: 'knight'};
		map[7][7] = {color:'white', pawn: 'whiteRook2', type: 'rook'};
		
		map[0][1] = {color:'black', pawn: 'blackPawn1', type: 'pawn', firstMove: true};
		map[1][1] = {color:'black', pawn: 'blackPawn2', type: 'pawn', firstMove: true};
		map[2][1] = {color:'black', pawn: 'blackPawn3', type: 'pawn', firstMove: true};
		map[3][1] = {color:'black', pawn: 'blackPawn4', type: 'pawn', firstMove: true};
		map[4][1] = {color:'black', pawn: 'blackPawn5', type: 'pawn', firstMove: true};
		map[5][1] = {color:'black', pawn: 'blackPawn6', type: 'pawn', firstMove: true};
		map[6][1] = {color:'black', pawn: 'blackPawn7', type: 'pawn', firstMove: true};
		map[7][1] = {color:'black', pawn: 'blackPawn8', type: 'pawn', firstMove: true};
		map[0][0] = {color:'black', pawn: 'blackRook1', type: 'rook'};
		map[1][0] = {color:'black', pawn: 'blackKnight1', type: 'knight'};
		map[2][0] = {color:'black', pawn: 'blackBishop1', type: 'bishop'};
		map[3][0] = {color:'black', pawn: 'blackQueen1', type: 'queen'};
		map[4][0] = {color:'black', pawn: 'blackKing1', type: 'king', firstMove: true};
		map[5][0] = {color:'black', pawn: 'blackBishop2', type: 'bishop'};
		map[6][0] = {color:'black', pawn: 'blackKnight2', type: 'knight'};
		map[7][0] = {color:'black', pawn: 'blackRook2', type: 'rook'};
	}

	function clickCancel() {
			var cl = document.getElementById('clicked');
			clicked = false;
			clickedField = undefined;		
			cl.setAttribute('x', 10000);
			cl.setAttribute('y', 10000);
			movesClear();
	}
	
	function movesClear() {
	   for (i in moves){
	       var elem = document.getElementById(moves[i]);
			elem.setAttribute('x', 10000);
			elem.setAttribute('y', 10000);
	   }
	   moves = [];
	}
	
	function boardClick(evt) {
		if (animation.dx != undefined) {
			animation.counter = 0;
		}
		if (color == undefined){
		   alert('Koniec gry!');
		   return;
		}
		movesClear();
		var cl = document.getElementById('clicked');
		var field = {x: Math.floor((evt.pageX)/100), y: Math.floor((evt.pageY)/100)};
		var x = field.x*100 + 2;
		var y = field.y*100 + 2;
		if (map[field.x][field.y].pawn == undefined) return;
		if (map[field.x][field.y].color != color) {
		     alert('Nie Twój ruch!');
		     return;
		}
		movesGenerate(field);
		cl.setAttribute('x', x);
		cl.setAttribute('y', y);
		clickedField = field;
		clicked = true;
    }
    
    function nextFrame() {
        if (animation.counter <= 0) {
            animation.obj.setAttribute('transform', "translate(" + (animation.end.x) + " " + (animation.end.y) + ") scale(2 2)");
            clearInterval(timer);
            animation = {};
            return;
        }
        --animation.counter;
        animation.x += animation.dx;
        animation.y += animation.dy;
        animation.obj.setAttribute('transform', "translate(" + (animation.x) + " " + (animation.y) + ") scale(2 2)");
    }
    
    function moveClick(evt){
		var cl = document.getElementById('clicked');
		var field = {x: Math.floor((evt.pageX)/100), y: Math.floor((evt.pageY)/100)};
			if (!moveOk(field, clickedField)) {
				alert('Ruch nieprawidłowy!');
				return;
			}
			if (map[field.x][field.y].pawn  !== undefined) {
				var pawn = document.getElementById(map[field.x][field.y].pawn);
				pawn.parentNode.removeChild(pawn);
				if (map[field.x][field.y].type == "king") {
				    alert('Koniec gry!');
				    color = undefined;
				}
			}
			
			var pawn = document.getElementById(map[clickedField.x][clickedField.y].pawn);
			if (!pawn) return;
			//pawn.setAttribute('transform', "translate(" + (field.x*100+8) + " " + (field.y*100+8) + ") scale(2 2)");
			animation.dx = (field.x - clickedField.x)*100/20;
			animation.dy = (field.y - clickedField.y)*100/20;
			animation.x = clickedField.x * 100 +8;
			animation.y = clickedField.y * 100 +8;
			animation.counter = 20;
			animation.end = {x: field.x*100 + 8, y: field.y*100 + 8};
			animation.obj = pawn;
			timer = setInterval("nextFrame()", 50);
            /*var transform = document.createElement('animateMotion');
            transform.setAttribute("from", "0,0");
            transform.setAttribute("to", ((clickedField.x - field.x)*100+8) + ","+ (field.y*100+8));
            transform.setAttribute("dur", "5s");
            transform.setAttribute("start", (Math.round(new Date().getTime() - time)/1000) + "s");
            transform.setAttribute("fill", "freeze");
            alert(transform.getAttribute("start"));
            pawn.appendChild(transform);*/
			map[field.x][field.y].pawn = map[clickedField.x][clickedField.y].pawn;
			map[field.x][field.y].color = map[clickedField.x][clickedField.y].color;
			map[field.x][field.y].type = map[clickedField.x][clickedField.y].type;
			map[clickedField.x][clickedField.y] = {};
			clickCancel();
			var newText;
			if (color == 'white'){
			     color = 'black';
			     document.getElementById("textStatus").textContent = 'Ruch: czarny'; 
			} else if (color == 'black') {
			     color = 'white';
			     document.getElementById("textStatus").textContent = 'Ruch: biały';
		    } else {
		          document.getElementById("textStatus").textContent = 'Koniec gry';
		    }
    }

	function movesGenerate(source) {
	    moves = [];
	    var destination = {};
	    var pattern = document.getElementById('move');
		for (var i=0; i<8; ++i) {
			for (var j=0; j<8; ++j){
			    if ( moveOk({x: i, y:j}, source) ) {
			        var elem = document.getElementById("move" + i + j)
			        if (!elem) {
			            elem = pattern.cloneNode(true);
				        elem.id = "move" + i + j;
				    }
				    elem.setAttribute('x', i*100+2);
				    elem.setAttribute('y', j*100+2);
                    pattern.parentNode.appendChild(elem);
				    moves.push(elem.id);
				}
			}
		}
	}
